def name_number #nombre d'entrée 
   
    puts "il y a #{JOURNALISTS.size} journalists dans cette liste"
    
end 



def num_num #combien de nom avec un numéro dedans
    bon_array = []
    for i in 0 ..9
        bon_array << JOURNALISTS.select { |str| str.include?('0')}
        bon_array << JOURNALISTS.select { |str| str.include?('1')}
        bon_array << JOURNALISTS.select { |str| str.include?('2')}
        bon_array << JOURNALISTS.select { |str| str.include?('3')}
        bon_array << JOURNALISTS.select { |str| str.include?('4')}
        bon_array << JOURNALISTS.select { |str| str.include?('5')}
        bon_array << JOURNALISTS.select { |str| str.include?('6')}
        bon_array << JOURNALISTS.select { |str| str.include?('7')}
        bon_array << JOURNALISTS.select { |str| str.include?('8')}
        bon_array << JOURNALISTS.select { |str| str.include?('9')}
    end
    puts "il y a #{bon_array.size} journaliste qui ont un chiffre dans leur tag twitter."
end


def with_Aude #nombre de nom contenant Aude (sans prendre en compte les majuscules)
    with_a = []
    with_a << JOURNALISTS.select { |str| str.downcase.include?("aude")} 
    puts "Il y a #{with_a.size} journalistes qui contiennent le mot 'aude' dans leurs tag."

end


def first_upper #Combien commencent par une majuscule
    fisrts_i = [] 
    fisrts_i << JOURNALISTS.count{|handle|/[[:upper:]]/.match(handle[1])}
    puts "il y a #{fisrts_i} journalists avec pour première lettre de leur tag une majuscule"
end




def num_upper # combien contiennent une majuscule
    exist_upeper = []
    exist_upeper << JOURNALISTS.count{|handle|/[[:upper:]]/.match(handle)}
    puts " il y a #{exist_upeper} journaliste dont le tag contient une majuscule"
end 


def numb_underscore #Combien de Underscore en tout
    under_how = []
    under_how << JOURNALISTS.count{|handle|/_/.match(handle)}
    puts "il y a #{under_how} journalists qui ont un underscore dans leur nom"

end 


def abc_order # Trier le liste dans l'ordre alphabetique
    puts JOURNALISTS.sort
end


def fifty_short # Montrer les 50 les plus cours dans la liste
   fifty =  []
    
        fifty << JOURNALISTS.sort_by(&:length)[0..49]
    
    puts fifty
end

def position_epenser # la position d'@epenser dans la liste
    puts JOURNALISTS.index("@epenser")
end


def perform
    name_number
    num_num
    with_Aude
    first_upper
    numb_underscore
    abc_order
    fifty_short
    position_epenser
end
perform